from enum import Enum

class ref_genome_sizes(Enum):
    def parse_refGenome_sizes(path):
        refGenome_sizes = {}
        pre = 0
        with open(path) as file:
            for line in file:
                chr, size_str = line.split()
                size = int(size_str)
                refGenome_sizes[chr] = (pre, pre + size - 1)
                pre = size
        return refGenome_sizes

